import React, { useState } from 'react';
import Input from '../../components/Input';
import PageHeader from '../../components/PageHeader';
import './styles.css';
import warningIcon from '../../assets/images/icons/warning.svg';
import Textarea from '../../components/Textarea';
import Select from '../../components/Select';


function TeacherForm() {
  const [name, setName] = useState('');
  const [avatar, setAvatar] = useState('');
  const [whatsapp, setWhatsapp] = useState('');
  const [bio, setBio] = useState('');

  const [subject, setSubject] = useState('')
  const [cost, setCost] = useState('')

  const [scheduleItems, setScheduleItems] = useState([
    { week_day: 0, from: '', to: '' }])

  function addNewScheduleItem() {
    setScheduleItems([
      ...scheduleItems,
      ({
        week_day: 0,
        from: '',
        to: ''
      })
    ]);

  }

  return (
    <div id="page-teacher-form" className="container">
      <PageHeader title="It's awesome you want to teach!."
        description="First, fill this form of scription"
      />

      <main>
        <fieldset>
          <legend>Your data</legend>

          <Input name="name" label="Full name" value={name}
            onChange={(e) => {
              setName(e.target.value)
            }} />
          <Input name="Avatar" label="Avatar" value={avatar}
            onChange={(e) => {
              setAvatar(e.target.value)
            }} />
          <Input name="whatsapp" label="Whatsapp" value={whatsapp}
            onChange={(e) => {
              setWhatsapp(e.target.value)
            }} />
          <Textarea name="bio" label="Biography" value={bio}
            onChange={(e) => {
              setBio(e.target.value)
            }} />
        </fieldset>
        <fieldset>
          <legend>About the class</legend>
          <Select
            name="subject"
            label="Subject"
            options={[
              { value: 'Artes', label: 'Artes' },
              { value: 'Biologia', label: 'Biologia' },
              { value: 'Matemática', label: 'Matemática' },
              { value: 'Ciências', label: 'Ciências' },
              { value: 'Geografia', label: 'Geografia' },
              { value: 'Ed. física', label: 'Ed. física' },
              { value: 'Português', label: 'Português' },
              { value: 'Filosofia', label: 'Filosofia' },
              { value: 'Química', label: 'Química' }

            ]}
          />
          <Input name="costPerClass" label="Cost per class" />
        </fieldset>
        <fieldset>
          <legend>
            Avaliable hours
            <button type="button" onClick={addNewScheduleItem}>
              + Novo horário
            </button>
          </legend>

          {scheduleItems.map(scheduleItem => {
            return (
              <div key={scheduleItem.week_day} className="schedule-item">
                <Select
                  name="week_day"
                  label="Day of week"
                  options={[
                    { value: '0', label: 'Sunday' },
                    { value: '1', label: 'Monday' },
                    { value: '2', label: 'Tuesday' },
                    { value: '3', label: 'Wednesdey' },
                    { value: '4', label: 'Thurdsay' },
                    { value: '5', label: 'Friday' },
                    { value: '6', label: 'Saturday' },
                  ]}
                />
                <Input name="from" label="Of" type="time" />
                <Input name="to" label="To" type="time" />
              </div>
            )
          })}

        </fieldset>
        <footer>
          <p>
            <img src={warningIcon} alt="Aviso importante" />
            Importante! <br />
            Preencha todos os dados!
          </p>
          <button type="button">
            Salvar cadastro
          </button>
        </footer>
      </main>


    </div>
  );
}

export default TeacherForm;
